function validate(){
    let e=document.querySelector(".name"),
        l=document.querySelector(".email"),
        t=document.querySelector(".message"),
        o=document.querySelector(".send-btn");
    
    o.addEventListener("click",
    o=>{o.preventDefault(),
        ""==e.value||""==l.value||""==t.value?emptyerror():
        (sendmail(e.value,l.value,t.value),success())
    })
}
        
    function sendmail(e,l,t){
        config.SERVICE_KEY,config.TEMPLATE_KEY;
        emailjs.send(
            "service_sidkxzp","template_mcxeppq",
            {to_name:l,from_name:e,message:t}
        )
    }
    
    function emptyerror(){
        swal({
            title:"Oh no....!",
            text:"Fields Cannot be empty!",
            icon:"error"
        })
    }
    
    function success(){
        swal({
            title:"Email sent successfully",
            text:"We try to reply in 24 hours",
            icon:"success"
        }),
        
        document.querySelector(".name").value="",
        document.querySelector(".email").value="",
        document.querySelector(".message").value=""
    }
    
    let menu=document.querySelector(".menu-icon"),
        navbar=document.querySelector(".navbar");
        menu.onclick=(()=>{
        navbar.classList.toggle("open-menu"),
        menu.classList.toggle("move")
    }),
    
    window.onscroll=(()=>{
        navbar.classList.remove("open-menu"),
        menu.classList.remove("move")
    });
    
    let header=document.querySelector("header");
        window.addEventListener("scroll",()=>{
        header.classList.toggle("header-active",window.scrollY>0)
    }),
    
    validate();
    
    let scrollTop=document.querySelector(".scroll-top");
        window.addEventListener("scroll",()=>{
        scrollTop.classList.toggle("scroll-active",window.scrollY>=400)
    });